import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useNavigate } from "react-router-dom";
import axios from "axios";
import './App.css'; // Import your CSS file

const EMIForm = ({ onSubmit }) => {
  const [loanAmount, setLoanAmount] = useState("");
  const [interestRate, setInterestRate] = useState("");
  const [tenure, setTenure] = useState("");
  const [emi, setEmi] = useState("");
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleCalculateClick = async () => {
    setError(null); // Reset error state
    try {
      const response = await axios.post("http://localhost:8989/calculator/cal/emi", {
        loanAmount: parseFloat(loanAmount),
        interestRate: parseFloat(interestRate),
        tenure: parseInt(tenure, 10)
      });

      setEmi(response.data.emi); // Update the EMI state

      onSubmit({
        loanAmount: parseFloat(loanAmount),
        interestRate: parseFloat(interestRate),
        tenure: parseInt(tenure, 10),
        emi: response.data.emi
      });

      navigate("/repayment-schedule");
    } catch (error) {
      console.error("Error calculating EMI:", error);
      setError("Error calculating EMI.");
    }
  };

  return (
    <div className="form-container">
      <h2>Calculate EMI</h2>
      <form>
        <label>
          Loan Amount:
          <input
            type="number"
            value={loanAmount}
            onChange={(e) => setLoanAmount(e.target.value)}
          />
        </label>
        <label>
          Interest Rate (%):
          <input
            type="number"
            value={interestRate}
            onChange={(e) => setInterestRate(e.target.value)}
          />
        </label>
        <label>
          Tenure (Months):
          <input
            type="number"
            value={tenure}
            onChange={(e) => setTenure(e.target.value)}
          />
        </label>
        <button type="button" onClick={handleCalculateClick}>
          Calculate EMI
        </button>
        {error && <p className="error">{error}</p>}
      </form>
      {emi && (
        <div className="emi-display">
          <h3>Calculated EMI: ₹{emi.toFixed(2)}</h3>
        </div>
      )}
    </div>
  );
};

const RepaymentSchedule = ({ loanDetails, onUpdate }) => {
  const [schedule, setSchedule] = useState([]);
  const [extraEmiValues, setExtraEmiValues] = useState({});
  const [error, setError] = useState(null);

  React.useEffect(() => {
    const fetchRepaymentSchedule = async () => {
      try {
        const response = await axios.post("http://localhost:8989/calculator/cal/schedule", {
          loanAmount: loanDetails.loanAmount,
          interestRate: loanDetails.interestRate,
          tenure: loanDetails.tenure,
          emi: loanDetails.emi
        });
        setSchedule(response.data);
      } catch (error) {
        console.error("Error fetching repayment schedule:", error);
        setError("Error fetching repayment schedule.");
      }
    };

    if (loanDetails) {
      fetchRepaymentSchedule();
    }
  }, [loanDetails]);

  const handleExtraEmiChange = (month, value) => {
    setExtraEmiValues(prev => ({
      ...prev,
      [month]: value
    }));
  };

  const handleSave = async () => {
    const emiList = new Array(loanDetails.tenure).fill(0).map((_, index) => extraEmiValues[index] || 0);

    try {
      const response = await axios.post("http://localhost:8989/calculator/cal/schedule/extraemi", {
        ...loanDetails,
        emiList
      });
      alert("Extra EMI values saved successfully!");
      onUpdate(response.data); // Update the schedule with the new data
    } catch (error) {
      console.error("Error saving extra EMI values:", error);
      setError("Error saving extra EMI values.");
    }
  };

  return (
    <div className="schedule-container">
      <h2>Repayment Schedule</h2>
      {error && <p className="error">{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Installment Date</th>
            <th>Loan Amount (₹)</th>
            <th>Interest Amount (₹)</th>
            <th>Principal Amount (₹)</th>
            <th>Installment (₹)</th>
            <th>Extra EMI (₹)</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <tr key={index}>
              <td>{item.installmentDate}</td>
              <td>{item.loanAmount.toFixed(2)}</td>
              <td>{item.interestAmount.toFixed(2)}</td>
              <td>{item.principalAmount.toFixed(2)}</td>
              <td>{item.installment.toFixed(2)}</td>
              <td>
                <input
                  type="number"
                  placeholder="Extra EMI"
                  value={extraEmiValues[index] || ''}
                  onChange={(e) => handleExtraEmiChange(index, e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave} className="save-btn">Save Extra EMI Values</button>
    </div>
  );
};

const App = () => {
  const [loanDetails, setLoanDetails] = useState(null);

  const handleLoanDetailsUpdate = (updatedSchedule) => {
    setLoanDetails(prevDetails => ({
      ...prevDetails,
      schedule: updatedSchedule
    }));
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<EMIForm onSubmit={setLoanDetails} />}
        />
        <Route
          path="/repayment-schedule"
          element={<RepaymentSchedule loanDetails={loanDetails} onUpdate={handleLoanDetailsUpdate} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
