import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import "./RepaymentSchedule.css"; // Import your CSS file

const RepaymentSchedule = () => {
  const location = useLocation();
  const [extraEmiValues, setExtraEmiValues] = useState({});
  const schedule = location.state?.schedule || [];

  // Assume these values come from the EMIForm page or are fixed
  const loanAmount = location.state?.loanAmount || 100000;
  const interestRate = location.state?.interestRate || 12;
  const tenure = location.state?.tenure || 12;
  const emi = location.state?.emi || 8885;

  const handleExtraEmiChange = (month, value) => {
    setExtraEmiValues(prev => ({
      ...prev,
      [month]: value
    }));
  };

  const handleSave = async () => {
    // Create emiList array with values from extraEmiValues
    const emiList = new Array(tenure).fill(0).map((_, index) => extraEmiValues[index] || 0);

    try {
      const response = await axios.post("http://localhost:8989/api/save-extra-emi", {
        loanAmount,
        interestRate,
        tenure,
        emi,
        emiList
      });
      console.log("Save response:", response.data);
      alert("Extra EMI values saved successfully!");
    } catch (error) {
      console.error("Error saving extra EMI values:", error);
    }
  };

  return (
    <div className="schedule-container">
      <h2>Repayment Schedule</h2>
      <table>
        <thead>
          <tr>
            <th>Installment Date</th>
            <th>Loan Amount (₹)</th>
            <th>Interest Amount (₹)</th>
            <th>Principal Amount (₹)</th>
            <th>Installment (₹)</th>
            <th>Extra EMI (₹)</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <tr key={index}>
              <td>{item.installmentDate}</td>
              <td>{item.loanAmount.toFixed(2)}</td>
              <td>{item.interestAmount.toFixed(2)}</td>
              <td>{item.principalAmount.toFixed(2)}</td>
              <td>{item.installment.toFixed(2)}</td>
              <td>
                <input
                  type="number"
                  placeholder="Extra EMI"
                  value={extraEmiValues[index] || ''}
                  onChange={(e) => handleExtraEmiChange(index, e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave} className="save-btn">Save Extra EMI Values</button>
    </div>
  );
};

export default RepaymentSchedule;
