import React, { useState, useEffect } from 'react';
import { Slider, Typography, Box } from '@mui/material';

const RangeSlider = ({ min, max, step, initialValue }) => {
  const [value, setValue] = useState(initialValue || min);
  const [inputValue, setInputValue] = useState(initialValue || min);

  useEffect(() => {
    // Ensure initial value is valid
    const validInitialValue = Math.max(min, Math.min(max, roundToNearestMultipleOf10(initialValue || min)));
    setValue(validInitialValue);
    setInputValue(validInitialValue);
  }, [min, max, initialValue]);

  const roundToNearestMultipleOf10 = (num) => {
    return Math.round(num / 10) * 10;
  };

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setValue(newValue);
    setInputValue(newValue);
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleInputBlur = () => {
    let newValue = parseInt(inputValue, 10);

    if (isNaN(newValue)) {
      newValue = value;
    } else {
      newValue = Math.max(min, Math.min(max, roundToNearestMultipleOf10(newValue)));
    }

    setValue(newValue);
    setInputValue(newValue);
  };

  return (
    <div>
      <input
        type="range"
        min={min}
        max={max}
        step={step}
        value={value}
        onChange={handleSliderChange}
      />
      <input
        type="number"
        value={inputValue}
        step={step}
        onChange={handleInputChange}
        onBlur={handleInputBlur}
        style={{ marginLeft: '10px' }}
      />
      <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px',width:'100px' }}>
        <Typography variant="body1">₹50,000</Typography>
        <Typography variant="body1">₹1,00,00,000</Typography>
      </Box>
    </div>
  );
};

export default RangeSlider;
