import React, { useState} from "react";
import {calculateEmi} from "./calculator";
import RepaymentSchedule from './RepaymentSchedule';
import { PieChart } from '@mui/x-charts/PieChart';
import { useDrawingArea } from '@mui/x-charts/hooks';
import { styled } from '@mui/material/styles';
import './App.css';

const EMIForm = ({ onCalculateEmi,loanDetails,onUpdate}) => {
    const [loanAmount, setLoanAmount] = useState("");
    const [interestRate, setInterestRate] = useState("");
    const [tenure, setTenure] = useState("");
    const [emi, setEmi] = useState(null);
    const [error, setError] = useState(null);
    // const navigate = useNavigate(); // Initialize useNavigate

    const handleCalculateClick = async () => {
      setError(null); // Reset error state
      try {
        const loanDetail = {
          loanAmount:parseFloat(loanAmount),
          interestRate: parseFloat(interestRate),
          tenure: parseInt(tenure, 10)
        };
         const response=calculateEmi(loanDetail);
         console.log("Response ",response);
        setEmi(response.emi); // Update the EMI state

        onCalculateEmi({
          loanAmount: parseFloat(loanAmount),
          interestRate: parseFloat(interestRate),
          tenure: parseInt(tenure, 10),
          emi: response.emi
        });

        // navigate("/repayment-schedule");
      } catch (error) {
        console.error("Error calculating EMI:", error);
        setError("Error calculating EMI.");
      }
    };
    const data = [
      { value: loanAmount, label: 'A' },
      { value: interestRate, label: 'B' },
      { value: tenure, label: 'C' },
      { value: emi, label: 'D' },
    ];

    const size = {
      width: 400,
      height: 200,
    };

    const StyledText = styled('text')(({ theme }) => ({
      fill: theme.palette.text.primary,
      textAnchor: 'middle',
      dominantBaseline: 'central',
      fontSize: 20,
    }));

    function PieCenterLabel({ children }) {
      const { width, height, left, top } = useDrawingArea();
      return (
        <StyledText x={left + width / 2} y={top + height / 2}>
          {children}
        </StyledText>
      );
    }

     function PieChartWithCenterLabel() {
      return (
        <PieChart series={[{ data, innerRadius: 80 }]} {...size}>
          <PieCenterLabel>Center label</PieCenterLabel>
        </PieChart>
      );
    }
    return (
      <>
      <div className="form-container">
        <h2>Calculate EMI</h2>
        <form>
          <label>
            Loan Amount:
            <input
              type="number"
              value={loanAmount}
              onChange={(e) => setLoanAmount(e.target.value)}
            />
          </label>
          <label>
            Interest Rate (%):
            <input
              type="number"
              value={interestRate}
              onChange={(e) => setInterestRate(e.target.value)}
            />
          </label>
          <label>
            Tenure (Months):
            <input
              type="number"
              value={tenure}
              onChange={(e) => setTenure(e.target.value)}
            />
          </label>
          <button type="button" onClick={handleCalculateClick}>
            Calculate EMI
          </button>
          {error && <p className="error">{error}</p>}
        </form>

      <div className="emi-display">{emi !== null ? `EMI: ${emi}` : ''}</div>
      </div>{
        loanDetails&&<RepaymentSchedule loanDetails={loanDetails} onUpdate={onUpdate} />
      }
      </>
    );
  };
  export default EMIForm;
