          <div className="emi-display">{emi !== null ? `EMI: ${emi}` : ""}</div>
        </div>
        {/* <div className="emi-display">
          <h1>Pie Chart with Center Label</h1>
          <PieChartWithCenterLabel
            data={pieChartData}
            label={`Total Emi: ${emi}`}
