import React, { useEffect, useState } from "react";
import axios from "axios";
import "./RepaymentSchedule.css";

const RepaymentSchedule = () => {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const fetchSchedule = async () => {
      try {
        const response = await axios.get("http://localhost:8989/calculator/cal/schedule");
        setSchedule(response.data);
      } catch (error) {
        console.error("Error fetching repayment schedule:", error);
      }
    };

    fetchSchedule();
  }, []);

  return (
    <div className="schedule-container">
      <h2>Repayment Schedule</h2>
      <table>
        <thead>
          <tr>
            <th>Installment Date</th>
            <th>Loan Amount (₹)</th>
            <th>Interest Amount (₹)</th>
            <th>Principal Amount (₹)</th>
            <th>Installment (₹)</th>
            <th>Extra EMI (₹)</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((item, index) => (
            <tr key={index}>
              <td>{item.installmentDate}</td>
              <td>{item.loanAmount.toFixed(2)}</td>
              <td>{item.interestAmount.toFixed(2)}</td>
              <td>{item.principalAmount.toFixed(2)}</td>
              <td>{item.installment.toFixed(2)}</td>
              <td>{item.extraEMI.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default RepaymentSchedule;
