import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import axios from "axios";
import RepaymentSchedule from "./RepaymentSchedule";
import "./App.css";

const EMIForm = () => {
  const [loanAmount, setLoanAmount] = useState("");
  const [interestRate, setInterestRate] = useState("");
  const [tenure, setTenure] = useState("");
  const [emi, setEmi] = useState(null);

  const calculateEMI = async () => {
    try {
      const response = await axios.post("http://localhost:8989/calculator/cal/emi", {
        loanAmount,
        interestRate,
        tenure,
      });
      setEmi(response.data.emi);
    } catch (error) {
      console.error("Error calculating EMI:", error);
    }
  };
  const fetchRepaymentSchedule = async () => {
    try {
      const response = await axios.post("http://localhost:8989/api/repayment-schedule", {
        loanAmount: loanAmount,
        interestRate: interestRate,
        tenure,
        emi,
      });
      // Navigate to the repayment schedule page and pass the data
      navigate("/repayment-schedule", { state: { schedule: response.data } });
    } catch (error) {
      console.error("Error fetching repayment schedule:", error);
    }
  };

  return (
    <div className="container">
      <h2>EMI Calculator</h2>
      <div className="form-group">
        <label>Principal Amount (₹)</label>
        <input
          type="number"
          placeholder="Enter LoanAmount"
          value={loanAmount}
          onChange={(e) => setLoanAmount(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Annual Interest Rate (%)</label>
        <input
          type="number"
          placeholder="Enter Interest Rate"
          value={interestRate}
          onChange={(e) => setInterestRate(e.target.value)}
        />
      </div>
      <div className="form-group">
        <label>Tenure (Months)</label>
        <input
          type="number"
          placeholder="Enter Tenure"
          value={tenure}
          onChange={(e) => setTenure(e.target.value)}
        />
      </div>
      <button className="calculate-btn" onClick={calculateEMI}>
        Calculate EMI
      </button>

      {emi !== null && (
        <div className="result">
          <h3>Your EMI is: ₹{emi}</h3>
          <button className="fetch-schedule-btn" onClick={fetchRepaymentSchedule}>
            View Repayment Schedule
          </button>
        </div>
      )} 
    </div>
  );
};

export default EMIForm;
