import React from "react";
import { Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend);

const DoubleDoughnutChart = () => {
  const innerData = {
    labels: ["A", "B", "C", "D"],
    datasets: [
      {
        data: [25, 25, 25, 25],
        backgroundColor: ["#6a0dad", "#1e90ff", "#20b2aa", "#ff6347"],
        borderWidth: 2,
      },
    ],
  };

  const outerData = {
    labels: ["E", "F", "G", "H", "I", "J"],
    datasets: [
      {
        data: [15, 15, 15, 15, 15, 15],
        backgroundColor: [
          "#ff00ff",
          "#1e90ff",
          "#20b2aa",
          "#6a0dad",
          "#ff6347",
          "#00bfff",
        ],
        borderWidth: 2,
      },
    ],
  };

  const innerOptions = {
    cutout: "70%", // Inner doughnut's cutout size
    rotation: 0,
    circumference: 360,
  };

  const outerOptions = {
    cutout: "50%", // Outer doughnut's cutout size, creating space between charts
    rotation: 0,
    circumference: 360,
  };

  return (
    <div style={{ position: "relative", width: "300px", height: "300px" }}>
      <div style={{ position: "absolute", width: "100%", height: "100%" }}>
        <Doughnut data={outerData} options={outerOptions} />
      </div>
      <div style={{ position: "absolute", width: "100%", height: "100%" }}>
        <Doughnut data={innerData} options={innerOptions} />
      </div>
    </div>
  );
};

export default DoubleDoughnutChart;
