import React, { useState, useEffect } from 'react';
import { Box, Typography } from '@mui/material';

const RangeSlider = ({ min, max, step, initialValue }) => {
  const [value, setValue] = useState(initialValue || min);
  const [inputValue, setInputValue] = useState(initialValue || min);

  useEffect(() => {
    // Ensure initial value is valid
    const validInitialValue = Math.max(min, Math.min(max, roundToNearestMultipleOf10(initialValue || min)));
    setValue(validInitialValue);
    setInputValue(validInitialValue);
  }, [min, max, initialValue]);

  const roundToNearestMultipleOf10 = (num) => {
    return Math.round(num / 10) * 10;
  };

  const handleSliderChange = (e) => {
    const newValue = parseInt(e.target.value, 10);
    setValue(newValue);
    setInputValue(newValue);
  };

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleInputBlur = () => {
    let newValue = parseInt(inputValue, 10);

    if (isNaN(newValue)) {
      newValue = value;
    } else {
      newValue = Math.max(min, Math.min(max, roundToNearestMultipleOf10(newValue)));
    }

    setValue(newValue);
    setInputValue(newValue);
  };

  return (
    <div>
      <Typography variant="body2">Loan Amount</Typography>
      <input
        type="range"
        min={min}
        max={max}
        step={step}
        value={value}
        onChange={handleSliderChange}
        style={{ width: '100%' }}
      />
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginTop: '10px'
        }}
      >
        <Typography variant="h5" component="span">
          ₹{value.toLocaleString()}
        </Typography>
        <input
          type="number"
          value={inputValue}
          step={step}
          onChange={handleInputChange}
          onBlur={handleInputBlur}
          style={{
            width: '100px',
            textAlign: 'right',
            marginLeft: '10px',
            padding: '5px',
            borderRadius: '5px',
            border: '1px solid #ccc'
          }}
        />
      </Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
        <Typography variant="body1">₹50,000</Typography>
        <Typography variant="body1">₹50,00,000</Typography>
      </Box>
    </div>
  );
};

export default RangeSlider;
